# List of new products to categorize
new_products = gifts
# Define stopwords
stop_words = set(stopwords.words('english'))

def extract_features(text):
    """
    Extract features from the text by tokenizing, removing stopwords,
    and creating a bag-of-words feature dictionary.
    """
    # Tokenize the text
    words = word_tokenize(text.lower())
    # Remove stopwords and non-alphabetic tokens
    filtered_words = [word for word in words if word.isalpha() and word not in stop_words]
    # Create a feature dictionary
    features = {word: True for word in filtered_words}
    return features

# Prepare the training set
training_set = [(extract_features(text), category) for (text, category) in training_data]

# Train the classifier
classifier = NaiveBayesClassifier.train(training_set)

def classify_product(text):
    """
    Classify a product based on its text description.
    """
    features = extract_features(text)
    return classifier.classify(features)

def classify_product_with_uncategorized(text, threshold=0.5):
    """
    Classify a product and return 'uncategorized' if the confidence is below the threshold.
    """
    features = extract_features(text)
    probabilities = classifier.prob_classify(features)
    top_category = probabilities.max()
    top_prob = probabilities.prob(top_category)
    if top_prob < threshold:
        return 'uncategorized'
    else:
        return top_category

# Categorize each new product without handling 'uncategorized'
for product in new_products:
    category = classify_product(product)

categorized_gifts = defaultdict(int)
number_unkowns = 0
unknowns = []
# Categorize each new product with 'uncategorized' handling
for product in new_products:
    category = classify_product_with_uncategorized(product)
    if category == 'uncategorized':
        number_unkowns += 1
        unknowns.append(product)
    else:
        categorized_gifts[category] += 1

# Define a function to categorize gifts based on keywords
def categorize_gift(gift_name):
    if any(keyword in gift_name.lower() for keyword in ['music', 'instrument', 'drum', 'guitar', 'piano', 'violin', 'trumpet', 'saxophone', 'flute', 'banjo', 'kazoo', 'triangle', 'keyboard']):
        return 'Musical Instruments'
    elif any(keyword in gift_name.lower() for keyword in ['art', 'drawing', 'painting', 'craft', 'sculpture', 'design', 'studio', 'pottery', 'jewelry']):
        return 'Arts and Crafts'
    elif any(keyword in gift_name.lower() for keyword in ['science', 'lab', 'kit', 'biology', 'chemistry', 'robot', 'engineering', 'math', 'coding', 'experiment', 'microscope', 'telescope']):
        return 'Educational Kit'
    elif any(keyword in gift_name.lower() for keyword in ['game', 'board', 'puzzle', 'strategy', 'trivia', 'chess', 'jenga', 'domino', 'card', 'dice', 'monopoly', 'yahtzee', 'clue', 'battleship']):
        return 'Games and Puzzles'
    elif any(keyword in gift_name.lower() for keyword in ['outdoor', 'camping', 'hiking', 'adventure', 'bicycle', 'skateboard', 'trampoline', 'kayak', 'fishing', 'sled', 'snowboard', 'tent', 'wagon']):
        return 'Outdoor Activities'
    elif any(keyword in gift_name.lower() for keyword in ['toy', 'figure', 'doll', 'lego', 'action', 'superhero', 'play', 'vehicle', 'car', 'truck', 'train', 'airplane', 'boat']):
        return 'Toys and Vehicles'
    elif any(keyword in gift_name.lower() for keyword in ['kitchen', 'cooking', 'food', 'baking', 'restaurant', 'candy', 'chef', 'ice cream', 'cotton candy']):
        return 'Food Cooking'
    elif any(keyword in gift_name.lower() for keyword in ['animal', 'zoo', 'farm', 'wildlife', 'pet', 'habitat', 'garden', 'insect', 'bug']):
        return 'Animal and Nature'
    else:
        return 'Miscellaneous'

for gift in unknowns:
    categorized_gifts[categorize_gift(gift)] += 1
categorized_gifts['Educational Kit'] += categorized_gifts['Educational Kits']
del categorized_gifts['Educational Kits']
un_category = categorized_gifts['Miscellaneous']
